package keel.GraphInterKeel.experiments;

import java.util.EventObject;

import keel.RunKeelTxtDocente.EducationalRunKeelTxt;

/* 
 * General description:
 * Events for RunKeelTxTDocente
 * 
 * Author: Juan Carlos Fernï¿½ndez Caballero
 * Date: 26-04-2006
 */
public class RunkeelEvent<A extends EducationalRunKeelTxt> extends EventObject {
    /////////////////////////////////////////////////////////////////
    // --------------------------------------- Serialization constant
    /////////////////////////////////////////////////////////////////

    /** Generated by Eclipse */
    private static final long serialVersionUID = -485187902934505060L;

    /////////////////////////////////////////////////////////////////
    // -------------------------------------------------- Propiedades
    /////////////////////////////////////////////////////////////////
    protected A runkeel;
    protected Exception exception;

    /////////////////////////////////////////////////////////////////
    // ------------------------------------------------ Constructores
    /////////////////////////////////////////////////////////////////
    public RunkeelEvent(A runkeel) {
        this(runkeel, null);
    }

    public RunkeelEvent(A runkeel, Exception exception) {
        super(runkeel);
        this.runkeel = runkeel;
        this.exception = exception;
    }

    /////////////////////////////////////////////////////////////////
    // ------------------------------------------------------ Metodos
    /////////////////////////////////////////////////////////////////
    public final A getRunkeel() {
        return runkeel;
    }

    public final Exception getException() {
        return exception;
    }
}
